for (const cfg of setConfigs) {
  for (let i = 0; i < cfg.qs.length; i++) {
    const raw = cfg.qs[i];
    const globalIdx = cfg.startNum + i;
    const id = `pqq-n2-${String(globalIdx).padStart(3, '0')}`;
    questions.push({
      id,
      quiz_set_id: cfg.setId,
      question_type: "multiple_choice",
      question_text: raw.q,
      question_audio_url: "",
      question_image_url: "",
      options: raw.opts,
      correct_answer: raw.ans,
      explanation: raw.exp,
      hint: "",
      difficulty: raw.diff,
      tags: raw.tags || cfg.defaultTags,
      order_index: i + 1,
    });
  }
}

